
// Set the date we're counting down to
var countDownDate = new Date("May 10, 2019 08:00:00").getTime();

// Update the count down every 1 second
var x = setInterval(function() {

  // Get todays date and time
  var now = new Date().getTime();

  // Find the distance between now and the count down date
  var distance = countDownDate - now;

  // Time calculations for days, hours, minutes and seconds
  var days = Math.floor(distance / (1000 * 60 * 60 * 24));
  var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  var seconds = Math.floor((distance % (1000 * 60)) / 1000);

  // Display the result in the element with id="demo"
  document.getElementById("days").innerHTML = days + "<p style='display: block;font-size: 20px;  font-weight: 300;  '>Days</p>    ";
  document.getElementById("hours").innerHTML = hours + "<p style='display: block;font-size: 20px;  font-weight: 300;  '>Hours</p>    ";
  document.getElementById("minutes").innerHTML = minutes + "<p style='display: block;font-size: 20px;  font-weight: 300;  '>Mins</p>    ";
  document.getElementById("seconds").innerHTML = seconds + "<p style='display: block;font-size: 20px;  font-weight: 300;  '>Secs</p>    ";

  // If the count down is finished, write some text 
  if (distance < 0) {
    clearInterval(x);
    document.getElementById("mu-event-counter").innerHTML = "EXPIRED";
  }
}, 1000);
